$('.horizontal-scroll').horizontalScroll({

	 
	
    // container height
  
    "containerHeight":"300vh",
  
  
    // image height
  
    "itemsHeight": base.maxHeight,
  
  
    // slider width
  
    "width": base.sumWidths.toString() +"px",
  
    // top padding
  
    "paddingTop": base.paddingTop,
  
    // left padding
  
    "paddingLeft": base.paddingLeft,
     
  });